mapper.select:
SELECT USER_ID, USER_EMAIL, JOINED_AT
  FROM USER_T
 WHERE USER_ID = #{userId}

SELECT USER_ID
  FROM SLEEP_USER_T
 WHERE USER_ID = #{userId}

SELECT USER_ID
  FROM LEAVE_USER_T
 WHERE USER_ID = #{userId}

SELECT USER_EMAIL
  FROM USER_T
 WHERE USER_EMAIL = #{userEmail}

SELECT USER_EMAIL
  FROM SLEEP_USER_T
 WHERE USER_EMAIL = #{userEmail}

SELECT USER_EMAIL
  FROM LEAVE_USER_T
 WHERE USER_EMAIL = #{userEmail}

mapper.insert:
INSERT INTO USER_T (
    USER_NO
  , USER_ID
  , USER_PASSWORD
  , USER_NAME
  , USER_GENDER
  , USER_EMAIL
  , USER_MOBILE
  , USER_BIRTHYEAR
  , USER_BIRTHDATE
  , POSTCODE
  , ROAD_ADDRESS
  , JIBUN_ADDRESS
  , DETAIL_ADDRESS
  , EXTRA_ADDRESS
  , AGREECODE
  , JOINED_AT
  , PW_MODIFIED_AT
  , AUTOLOGIN_ID
  , AUTOLOGIN_EXPIRED_AT
) VALUES (
    USER_SEQ.NEXTVAL
  , #{userId}
  , #{userPassword}
  , #{userName}
  , #{userGender}
  , #{userEmail}
  , #{userMobile}
  , #{userBirthyear}
  , #{userBirthdate}
  , #{postcode}
  , #{roadAddress}
  , #{jibunAddress}
  , #{detailAddress}
  , #{extraAddress}
  , #{agreecode}
  , SYSDATE
  , SYSDATE
  , NULL
  , NULL
)

mapper.select:
SELECT USER_ID, USER_NO
  FROM USER_T
 WHERE USER_ID = #{userId}
   AND USER_PASSWORD = #{userPassword}

mapper.insert:
INSERT INTO USER_ACCESS_T (
    USER_ID
  , LAST_LOGIN_AT
) VALUES (
    #{userId}
  , SYSDATE
)

mapper.update:
UPDATE USER_ACCESS_T
   SET LAST_LOGIN_AT = SYSDATE
 WHERE USER_ID = #{userId}

UPDATE USER_T
   SET AUTOLOGIN_ID = #{autologinId}
     , AUTOLOGIN_EXPIRED_AT = #{autologinExpiredAt}
 WHERE USER_ID = #{userId}

mapper.insert:
INSERT INTO LEAVE_USER_T (
    USER_ID
  , USER_EMAIL
  , JOINED_AT
  , LEAVED_AT
) VALUES (
    #{userId}
  , #{userEmail}
  , #{joinedAt}
  , SYSDATE
)
